import React, { Component } from "react";
import ItemManager from "./contracts/ItemManager.json";
import Item from "./contracts/Item.json";
import getWeb3 from "./getWeb3";

import "./App.css";

class App extends Component {
  state = { loaded: false, cost: 0, itemName: "null" };

  componentDidMount = async () => {
    try {
      // Get network provider and web3 instance.
      this.web3 = await getWeb3();

      // Use web3 to get the user's accounts.
      this.accounts = await web3.eth.getAccounts();

      // Get the contract instance.
      this.networkId = await this.web3.eth.net.getId();
      this.itemManager = new web3.eth.Contract(
        ItemManager.abi,
        ItemManager.networks[networkId] &&
          ItemManager.networks[networkId].address
      );

      this.item = new web3.eth.Contract(
        Item.abi,
        Item.networks[networkId] && Item.networks[networkId].address
      );

      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      this.setState({ loaded: true });
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`
      );
      console.error(error);
    }
  };

  handleInputChange = (event) => {
    const target = event.target;
    const value = target.type == "checkbox" ? target.checkrd : target.value;
    const name = target.name;
    this.setState({ [name]: value });
  };

  handleSubmit = async () => {
    const { cot, itemName } = this.state;
    await this.itemManager.methods
      .createItem(itenName, cost)
      .send({ from: this.accounts[0] });
  };

  runExample = async () => {
    const { accounts, contract } = this.state;

    // Stores a given value, 5 by default.
    await contract.methods.set(69).send({ from: accounts[0] });

    // Get the value from the contract to prove it worked.
    const response = await contract.methods.get().call();

    // Update state with the result.
    this.setState({ storageValue: response });
  };

  render() {
    if (!this.state.loaded) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return (
      <div className="App">
        <h1>AutoChain 1.0 test</h1>
        <h2>Items</h2>
        <h2>Add Items</h2>
        <p>
          Cost in wei:{" "}
          <input
            type="text"
            name="cost"
            value="this.state.cost"
            onChange={this.handleInputChange}
          />
          Item Identifier:{" "}
          <input
            type="text"
            name="itemName"
            value="this.state.itemName"
            onChange={this.handleInputChange}
          />
          <button type="button" onClick={this.handleSubmit}></button>
        </p>
      </div>
    );
  }
}

export default App;
